import { HttpClient } from "@angular/common/http";
import { Injectable } from '@angular/core';
import { Observable } from "rxjs";
import { environment } from "src/environments/environment.development";
import { Traveller } from "../models/traveller.model";

@Injectable({
  providedIn: 'root'
})
export class TravellersService {

  baseApiUrl: string = environment.baseApiUrl;

  constructor(private http: HttpClient) { }

  getAllTravellers(): Observable<Traveller[]> {
    return this.http.get<Traveller[]>(this.baseApiUrl + "api/Traveller");
  }

  createTraveller(addTravellerRequest: Traveller): Observable<Traveller> {
    return this.http.post<Traveller>(this.baseApiUrl + "api/Traveller", addTravellerRequest);
  }
}
